mapper.select:

      select * from `macaovehicle_driver_loss` where 1=1
      
          AND `formInstCode` like concat('%',#{formInstCode},'%') 
      
      
      	and `newDriverIc` like concat('%',#{newDriverIc},'%')
      
      
     		and recordTime BETWEEN #{applyTimeFront} AND #{applyTimeBack}
      
      limit #{start},#{count}
