mapper.select:

      select * from `macaovehicle_tasking_ciq` where 1=1 and `status` != 0
      
      	and `ciqStatus` = #{ciqStatus}
      
      
      	and `status` != #{statusIgnore}
      
      
      	and `status` = #{status}
      
      
      	and `type` = #{type}
      
      
          AND `applyType` = #{applyType}
      
      
          AND `formInstCode` like concat('%',#{formInstCode},'%') 
      
      
      	AND `ownerName` like concat('%',#{ownerName},'%')
      
      and `clerkName` like concat('%',#{clerkName},'%') 
      and `idCard` like concat('%',#{idCard},'%')
      and `companyName` like concat('%',#{companyName},'%')
      and `companyCode` like concat('%',#{companyCode},'%')
      and `plateNumber` like concat('%',#{plateNumber},'%')
      and applyTime BETWEEN #{applyTimeFront} AND #{applyTimeBack}
      limit #{start},#{count}


      select count(*) from `macaovehicle_tasking_ciq` where 1=1 and `status` != 0
        
      	and `ciqStatus` = #{ciqStatus}
      
      
      	and `status` != #{statusIgnore}
      
      
      	and `status` = #{status}
      
      
      	and `type` = #{type}
      
      
          AND `applyType` = #{applyType}
      
      
          AND `formInstCode` like concat('%',#{formInstCode},'%') 
      
      
      	AND `ownerName` like concat('%',#{ownerName},'%')
      
      and `clerkName` like concat('%',#{clerkName},'%') 
      and `idCard` like concat('%',#{idCard},'%')
      and `companyName` like concat('%',#{companyName},'%')
      and `companyCode` like concat('%',#{companyCode},'%')
      and `plateNumber` like concat('%',#{plateNumber},'%')
      and applyTime BETWEEN #{applyTimeFront} AND #{applyTimeBack}
